var link=document.querySelector(".find-link");var popap=document.querySelector(".contener-form");var datein=popap.querySelector(".date-in");var dateout=popap.querySelector(".date-out");var adults=popap.querySelector("#adults");var kids=popap.querySelector("#kids");var form=popap.querySelector("form");link.addEventListener("click",function(a){a.preventDefault();popap.classList.toggle("modal-show");datein.focus();popap.classList.add("modal-animation")});form.addEventListener("submit",function(a){if(!datein.value||!dateout.value){a.preventDefault();console.log("Введите дату заезда и дату выезда")}else{if(!adults.value||!kids.value){a.preventDefault();console.log("Введите количество взрослых и детей")}}});window.addEventListener("keydown",function(a){if(a.keyCode===27){a.preventDefault();if(popap.classList.contains("contener-form")){popap.classList.add("modal-show")}}});
